import { Component } from '@angular/core';
import { Book } from '../models/book';
import { ActivatedRoute, Router } from '@angular/router';
import { BookService } from '../services/book.service';

@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css'],
})
export class EditComponent {
  book: Book = {
    id: -1,
    title: '',
    author: '',
    editor: '',
    publishDate: new Date(),
  };
  constructor(
    private actRoute: ActivatedRoute,
    private router: Router,
    private BookService: BookService
  ) {}

  ngOnInit() {
    this.actRoute.paramMap.subscribe((params) => {
      // Get bookId param passed from the other route.
      const id = params.get('bookId');
      // The id needs to be number !
      const book = this.BookService.getBookById(Number(id));
      if (book) {
        this.book = book;
        return;
      }
      this.router.navigate(['/list']);
    });
  }
  edit() {
    // Object destructuring to insure that the id is a number. since i had an issue while putting something different then a number
    const newBook = {
      ...this.book,
      id: Number(this.book.id),
    };
    // updates the actual book.
    this.BookService.updateBook(newBook);
    this.router.navigate(['/list']);
  }
}
<h2 class="text-3xl my-4">Edit a book</h2>
<form #f="ngForm">
  <div class="grid gap-6 mb-6 md:grid-cols-2 p-4">
    <div>
      <label for="" class="block mb-2 text-sm font-medium text-gray-900"
        >ID</label
      >
      <input
        type="text"
        class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5"
        ngModel
        [(ngModel)]="book.id"
        name="id"
      />
    </div>
    <div>
      <label for="" class="block mb-2 text-sm font-medium text-gray-900">
        title</label
      >
      <input
        type="text"
        class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5"
        ngModel
        [(ngModel)]="book.title"
        name="title"
      />
    </div>

    <div>
      <label for="" class="block mb-2 text-sm font-medium text-gray-900">
        Author</label
      >
      <input
        type="text"
        class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5"
        ngModel
        [(ngModel)]="book.author"
        name="author"
      />
    </div>
    <div>
      <label for="" class="block mb-2 text-sm font-medium text-gray-900">
        Editor</label
      >
      <input
        type="text"
        class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5"
        ngModel
        name="editor"
        [(ngModel)]="book.editor"
      />
    </div>
  </div>

  <button
    class="mx-2 text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 mr-2 mb-2 dark:bg-blue-600 dark:hover:bg-blue-700 focus:outline-none dark:focus:ring-blue-800"
    type="submit"
    (click)="edit()"
  >
    Edit
  </button>
</form>
