import { Injectable } from '@angular/core';
import { Book } from '../models/book';

/**
 * Service implementation to manage book crud
 */

@Injectable({
  providedIn: 'root',
})
export class BookService {
  constructor() {}

  /**
   * Initialized Data
   *
   * @type {Book[]}
   * @memberof BookService
   */
  list: Book[] = [
    {
      id: 1,
      title: 'Learn JS',
      author: 'Aloui Khalil',
      editor: 'Aloui Khalil',
      publishDate: new Date(),
    },
  ];

  /**
   * Get a book by id
   * @param id book id
   * @returns
   */
  getBookById(id: number) {
    return this.list.find((book) => book.id === id);
  }

  /**
   * Add a new book to the list
   * @param book
   * @returns
   */
  addBook(book: Book) {
    this.list.push({ ...book, publishDate: new Date() });
    return this.list;
  }

  filterBookById(id: number) {
    return this.list.filter((_book) => _book.id !== id);
  }

  deleteBook(id: number) {
    const books = this.filterBookById(id);
    this.list = books;
    return books;
  }

  /**
   * Updates specific book
   * @param book
   */
  updateBook(book: Book) {
    // get the list without the updated book
    const books = this.filterBookById(book.id);
    // add the book to the new list.
    this.list = [...books, book];
  }
}
